INSERT INTO t1 (n_star, short, sentiment) values (3, '最后，汤川也在走向加贺一样，每个人终其一身都是在逃离自身经历', 0.999724231619343);
INSERT INTO t1 (n_star, short, sentiment) values (4, '故事整体不能和《嫌疑人X的献身》比肩，希望改编成影视的效果会好一点；边看书边脑补福山雅治饰演汤川学的画面感。', 0.9655489580137296);
INSERT INTO t1 (n_star, short, sentiment) values (3, '说实话对汤川感受不深', 0.7045119244407565);
INSERT INTO t1 (n_star, short, sentiment) values (3, '长岗修要想知道古之秋穗死了的这件事不用一定要问别人吧？', 0.9146811757741707);
