INSERT INTO t1 (n_star, short, sentiment) values (3, '50页的短篇写成300页的长篇，东野老贼也网文化了，昏昏欲睡的一本小说', 0.9701565713285868);
INSERT INTO t1 (n_star, short, sentiment) values (5, '本来看着这个书名和开头的内容一点点引人入胜，是不错的题材，但最后结局没有任何新意，没有灵魂的对撞，没有关键人物的死亡，没有引发我任何的思考，看完觉得空虚的一本书，给3分吧，除了结局，内容还是充实的，封面也还不错。', 0.9999999999854228);
INSERT INTO t1 (n_star, short, sentiment) values (4, '书写的很好啦，但单单把禁忌魔术最后一个短篇拿出来，还扩写了，这个是神马操作？？？！！！真心好迷。。。', 0.9999772691928481);
INSERT INTO t1 (n_star, short, sentiment) values (5, '科学技术是双刃剑呀，关键在于使用者。
